// /components/icons/index.ts
import React from 'react'

export interface IconProps {
  className?: string
  size?: number
  width?: number
  height?: number
  fill?: string
}

// Base Icon Component for consistent behavior
const BaseIcon: React.FC<
  IconProps & {
    viewBox: string
    children: React.ReactNode
    defaultSize?: number
  }
> = ({
  className,
  size,
  width,
  height,
  fill = 'currentColor',
  viewBox,
  children,
  defaultSize = 24,
}) => {
  const iconSize = size || defaultSize
  return (
    <svg
      width={width || iconSize}
      height={height || iconSize}
      viewBox={viewBox}
      fill={fill}
      className={className}
      xmlns="http://www.w3.org/2000/svg"
    >
      {children}
    </svg>
  )
}

