// ===================
// ICON REGISTRY & UTILITIES
// ===================

export const ALL_ICONS = {
  ...SOCIAL_ICONS,
  ...TECH_ICONS,
  ...BENEFIT_ICONS,
  ...UI_ICONS,
} as const

export type IconName = keyof typeof ALL_ICONS
export type SocialPlatform = keyof typeof SOCIAL_ICONS
export type TechIcon = keyof typeof TECH_ICONS
export type BenefitIcon = keyof typeof BENEFIT_ICONS
export type UIIcon = keyof typeof UI_ICONS

// Universal Icon component that can render any icon by name
export const Icon: React.FC<IconProps & { name: IconName }> = ({ name, ...props }) => {
  const IconComponent = ALL_ICONS[name]
  return IconComponent ? <IconComponent {...props} /> : null
}

// Utility function to get icon by name (useful for dynamic rendering)
export const getIcon = (name: string): React.FC<IconProps> | null => {
  return ALL_ICONS[name as IconName] || null
}

// Utility function to check if icon exists
export const hasIcon = (name: string): name is IconName => {
  return name in ALL_ICONS
}
